const darkSwitch=document.getElementById("darkSwitch");window.addEventListener("load",()=>{darkSwitch&&(initTheme(),darkSwitch.addEventListener("change",()=>{resetTheme()}))});const darkModeElements=new Set([document.body]);function initTheme(){const e=null!==localStorage.getItem("darkSwitch")&&"dark"===localStorage.getItem("darkSwitch");darkSwitch.checked=e,e?darkModeElements.forEach(function(e){e.setAttribute("data-theme","dark")}):darkModeElements.forEach(function(e){e.removeAttribute("data-theme")})}function resetTheme(){darkSwitch.checked?(darkModeElements.forEach(function(e){e.setAttribute("data-theme","dark")}),localStorage.setItem("darkSwitch","dark")):(darkModeElements.forEach(function(e){e.removeAttribute("data-theme")}),localStorage.removeItem("darkSwitch"))}
